// CustomNode.js
import React from 'react';
import { Handle } from 'reactflow';

const CustomNode = ({ data }) => {
  return (
    <div style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '5px', backgroundColor: '#fff' }}>
      <div className='node' style={{ display: 'flex', alignItems: 'center' }}>
        <div>
        {data.icon && <span class="material-symbols-outlined">chat</span>}
        <div className='node-heading'>Send Message</div>
        </div>
        <div style={{ margin: 0 }}>{data.label}</div>
      </div>
      <Handle
        type="target"
        position="left"
        style={{ background: '#555' }}
      />
      <Handle
        type="source"
        position="right"
        style={{ background: '#555' }}
      />
    </div>
  );
};

export default CustomNode;
