import React, { useState, useRef, useCallback } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  useNodesState,
  useEdgesState,
  Controls,
  Background,
} from 'reactflow';
import 'reactflow/dist/style.css';

import Sidebar from './Sidebar';
import CustomNode from './CustomNode'; // Import Custom Node Component
import './index.css';
import Topbar from './Topbar';

const initialNodes = [
  {
    id: '1',
    type: 'customNode', // Specify the custom node type
    data: { label: 'Custom Node' },
    position: { x: 250, y: 20 },
    sourcePosition: 'right',
    targetPosition: 'left',
  },
  {
    id: '2',
    type: 'customNode', // Specify the custom node type
    data: { label: 'Custom Node' },
    position: { x: 550, y: 120 },
    sourcePosition: 'right',
    targetPosition: 'left',
  },
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2' },
];

let id = 0;
const getId = () => `dndnode_${id++}`;

const DnDFlow = () => {
  const reactFlowWrapper = useRef(null);
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);
  const [selectedNode, setSelectedNode] = useState(null);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [],
  );

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();

      const type = event.dataTransfer.getData('application/reactflow');

      const position = reactFlowInstance.project({
        x: event.clientX,
        y: event.clientY,
      });
      const newNode = {
        id: getId(),
        type: 'customNode', // Specify the custom node type
        sourcePosition: 'right',
        targetPosition: 'left',
        position,
        data: { label: 'New Custom Node' },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [reactFlowInstance, setNodes],
  );

  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);

  const updateNodeLabel = (newLabel) => {
    if (selectedNode) {
      setNodes((nds) =>
        nds.map((n) =>
          n.id === selectedNode.id
            ? {
                ...n,
                data: { ...n.data, label: newLabel },
              }
            : n
        )
      );
    }
  };

  const nodeTypes = { customNode: CustomNode };

  return (
    <div className='screen'>
      <Topbar />
      <div className="dndflow">
        <ReactFlowProvider>
          <div className="reactflow-wrapper" ref={reactFlowWrapper}>
            <ReactFlow
              nodes={nodes}
              edges={edges}
              onNodesChange={onNodesChange}
              onEdgesChange={onEdgesChange}
              onConnect={onConnect}
              onInit={setReactFlowInstance}
              onDrop={onDrop}
              onDragOver={onDragOver}
              onNodeClick={onNodeClick}
              nodeTypes={nodeTypes} // Register the custom node types
            >
              <Controls />
              <Background variant="dots" gap={12} size={1} />
            </ReactFlow>
          </div>
          <Sidebar />
          {selectedNode && (
            <div>
              <input
                type="text"
                value={selectedNode.data.label}
                onChange={(e) => updateNodeLabel(e.target.value)}
              />
            </div>
          )}
        </ReactFlowProvider>
      </div>
    </div>
  );
};

export default DnDFlow;
