import React, { useEffect, useState } from 'react';

const SettingsPanel = ({ selectedNode, updateNodeLabel, setSelectedNode }) => {
  const [input, setInput] = useState(selectedNode.data.label)

  const updateHandler = () => {
    updateNodeLabel(input)
  }

  const backBtnHandler= () => {
    setSelectedNode(null)
  }

  useEffect(()=> {
    setInput(selectedNode.data.label)
  }, [selectedNode])

  if (!selectedNode) {
    return <div className="settings-panel">Select a node to edit its label</div>;
  }

  return (
    <aside className="settings-panel">
      <div className='heading setting-heading'>
      <span className="material-symbols-outlined icon" onClick={backBtnHandler}>
        arrow_back
      </span>
      <div>
        Settings Panel
      </div>
      </div>
      <div className='main'>
        <div className="description">Edit the label of the selected node:</div>
        <textarea rows="5"
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={updateHandler} className='update-btn' disabled={disableUpdateBtn}>Update</button>
      </div>
    </aside>
  );
};

export default SettingsPanel;
