import React, { useState } from 'react';

const SettingsPanel = ({ selectedNode, updateNodeLabel }) => {
  const [input, setInput] = useState(selectedNode.data.label)
  if (!selectedNode) {
    return <div className="settings-panel">Select a node to edit its label</div>;
  }

  return (
    <aside className="settings-panel">
      <div className='heading'>
        Settings Panel
      </div>
      <div className='main'>
        <div className="description">Edit the label of the selected node:</div>
        <textarea rows="5"
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={updateHandler}>Update</button>
      </div>
    </aside>
  );
};

export default SettingsPanel;
